import sysimport os# Bir üst klasöre çık, sonra 'ui' klasörüne gitsys.path.append(os.path.abspath(os.path.join(os.getcwd(), 'ui')))from main_window import MainWindow  # Doğru modül adı ile içe aktarfrom car_save_update import CarSaveUpdateimport customtkinter as ctkdef main():    # Uygulama penceresini oluştur    ctk.set_appearance_mode("dark")  # Karanlık mod (Opsiyonel)    ctk.set_default_color_theme("blue")  # Varsayılan tema    # Ana pencereyi oluştur    root = ctk.CTk()    # MainWindow sınıfını çağır    window = CarSaveUpdate(root)    # Uygulamayı çalıştır    root.mainloop()if __name__ == "__main__":    main()