import sqlite3import osclass DbConnector:    def __init__(self):        self.db_path = os.path.join("db.db")        self.conn = None        def connect(self):        if self.conn is None:            self.conn = sqlite3.connect(self.db_path)            print(f"Bağlanılan veritabanı: {self.db_path}")        return self.conn        def close(self):        if self.conn:            self.conn.close()            self.conn = None