import customtkinter as ctkclass MainWindow:    def __init__(self, root):        self.root = root        self.root.geometry("700x400")        self.root.title("CustomTkinter UI")        self.root.configure(bg="gray15")  # Arka plan sidebar ile uyumlu yapıldı        # Sidebar başlangıçta açık olacak        self.sidebar_visible = True        # Ana ekran butonlarını içeren frame        self.main_frame = ctk.CTkFrame(self.root, fg_color="gray15")        self.main_frame.pack(fill="both", expand=True)        # Üst kısma arama alanı ekleme        self.search_entry = ctk.CTkEntry(self.main_frame, placeholder_text="Ara...", width=300, height=30)        self.search_entry.pack(padx=60, pady=10)        # Sidebar (Yan Panel)        self.sidebar = ctk.CTkFrame(self.root, width=150, height=400, corner_radius=10, fg_color="gray25")        self.sidebar.place(x=-150, y=10)  # İlk başta gizlenmiş olarak başlıyor        # Sidebar içeriği        self.sidebar_button = ctk.CTkButton(self.sidebar, text="Menü Butonu")        self.sidebar_button.pack(pady=40, padx=10)        # Sidebar açıkken içinde olacak toggle buton        self.toggle_button = ctk.CTkButton(self.sidebar, text="X", width=30, height=30, font=("Arial", 14, "bold"),                                           corner_radius=20, fg_color="gray30", hover_color="gray40",                                           text_color="white", command=self.toggle_sidebar)        self.toggle_button.place(x=60, y=0)  # Sidebar'ın içinde bir yere yerleştirildi        # Ana ekran butonları        self.main_button = ctk.CTkButton(self.main_frame, text="Ana Buton")        self.main_button.pack(pady=20)        # Sidebar'ı açılışta animasyonla getirme        self.animate_sidebar(opening=True)    def animate_sidebar(self, opening=True):        """ Sidebar'ı yumuşak bir şekilde açıp kapatır. """        target_x = 10 if opening else -150        step = 5 if opening else -5        def step_animation():            current_x = self.sidebar.winfo_x()            if (opening and current_x < target_x) or (not opening and current_x > target_x):                self.sidebar.place(x=current_x + step, y=10)                self.root.after(10, step_animation)  # 10ms gecikme ile animasyon            else:                self.sidebar.place(x=target_x, y=10)        step_animation()    def toggle_sidebar(self):        if self.sidebar_visible:            self.animate_sidebar(opening=False)  # Sidebar'ı kapat            self.toggle_button.place_forget()            self.toggle_button = ctk.CTkButton(self.root, text="☰", width=30, height=30, font=("Arial", 14, "bold"),                                               corner_radius=15, fg_color="gray30", hover_color="gray40",                                               text_color="white", command=self.toggle_sidebar)            self.toggle_button.place(x=10, y=10)  # Pencerenin sol üstüne yerleştir            self.sidebar_visible = False        else:            self.animate_sidebar(opening=True)  # Sidebar'ı aç            self.toggle_button.place_forget()            self.toggle_button = ctk.CTkButton(self.sidebar, text="X", width=30, height=30, font=("Arial", 14, "bold"),                                               corner_radius=20, fg_color="gray30", hover_color="gray40",                                               text_color="white", command=self.toggle_sidebar)            self.toggle_button.place(x=60, y=0)            self.sidebar_visible = True