import customtkinter as ctkclass MainWindow:    def __init__(self, root):        self.root = root        self.root.geometry("700x400")        self.root.title("Araç Takip")        self.root.configure(bg="gray15")  # Arka plan sidebar ile uyumlu yapıldı        # Sidebar başlangıçta açık olacak        self.sidebar_visible = True        # Ana ekran butonlarını içeren frame        self.main_frame = ctk.CTkFrame(self.root, fg_color="gray15")        self.main_frame.pack(fill="both", expand=True)        # Arama çubuğu ve butonu aynı satıra almak için bir frame        self.search_frame = ctk.CTkFrame(self.main_frame, fg_color="gray15")        self.search_frame.pack(pady=10)        # Arama alanı        self.search_entry = ctk.CTkEntry(self.search_frame, placeholder_text="Ara...", width=300, height=30)        self.search_entry.grid(row=0, column=0, padx=(20, 5))  # Sağda buton olduğu için sağ padding az        # Arama butonu        self.search_button = ctk.CTkButton(self.search_frame, text="🔍", width=50, height=30, command=self.search_action)        self.search_button.grid(row=0, column=1, padx=(5, 20))  # Solda arama alanı olduğu için sol padding az        # Sidebar (Yan Panel)        self.sidebar = ctk.CTkFrame(self.root, width=150, height=400, corner_radius=10, fg_color="gray25")        self.sidebar.place(x=-150, y=10)  # İlk başta gizlenmiş olarak başlıyor        # Sidebar içeriği        self.sidebar_button = ctk.CTkButton(self.sidebar, text="Menü Butonu")        self.sidebar_button.pack(pady=40, padx=10)        # Sidebar içindeki toggle butonu        self.toggle_button = None        self.create_toggle_button()        # Ana ekran butonları        self.main_button = ctk.CTkButton(self.main_frame, text="Ana Buton")        self.main_button.pack(pady=20)        # Sidebar'ı açılışta animasyonla getirme        self.animate_sidebar(opening=True)    def search_action(self):        """Arama butonuna basıldığında çalışacak fonksiyon"""        search_text = self.search_entry.get()        print(f"Arama yapılıyor: {search_text}")  # Burayı kendi ihtiyacına göre güncelleyebilirsin    def animate_sidebar(self, opening=True):        """ Sidebar'ı yumuşak bir şekilde açıp kapatır. """        target_x = 10 if opening else -150        current_x = self.sidebar.winfo_x()        step = max(abs(target_x - current_x) // 10, 5)  # Mesafeye bağlı olarak adım belirleniyor        def step_animation():            nonlocal current_x            if (opening and current_x < target_x) or (not opening and current_x > target_x):                current_x += step if opening else -step                self.sidebar.place(x=current_x, y=10)                self.root.after(10, step_animation)  # 10ms gecikme ile animasyon            else:                self.sidebar.place(x=target_x, y=10)        step_animation()    def create_toggle_button(self):        """ Sidebar görünümüne göre toggle butonunu oluşturur. """        if self.toggle_button:            self.toggle_button.place_forget()        if self.sidebar_visible:            self.toggle_button = ctk.CTkButton(self.sidebar, text="X", width=30, height=30, font=("Arial", 14, "bold"),                                               corner_radius=20, fg_color="gray30", hover_color="gray40",                                               text_color="white", command=self.toggle_sidebar)            self.toggle_button.place(x=60, y=0)        else:            self.toggle_button = ctk.CTkButton(self.root, text="☰", width=30, height=30, font=("Arial", 14, "bold"),                                               corner_radius=15, fg_color="gray30", hover_color="gray40",                                               text_color="white", command=self.toggle_sidebar)            self.toggle_button.place(x=10, y=10)    def toggle_sidebar(self):        """ Sidebar açma/kapatma işlemi. """        self.animate_sidebar(opening=not self.sidebar_visible)        self.sidebar_visible = not self.sidebar_visible        self.create_toggle_button()