import customtkinter as ctkclass MainWindow:    def __init__(self, root):        self.root = root        self.root.geometry("700x400")        self.root.title("AraÃ§ Takip")        self.root.configure(bg="gray15")  # Arka plan sidebar ile uyumlu yapÄ±ldÄ±        # Sidebar baÅŸlangÄ±Ã§ta aÃ§Ä±k olacak        self.sidebar_visible = True        # Ana ekran butonlarÄ±nÄ± iÃ§eren frame        self.main_frame = ctk.CTkFrame(self.root, fg_color="gray15")        self.main_frame.pack(fill="both", expand=True)        # Arama Ã§ubuÄŸu ve butonu aynÄ± satÄ±ra almak iÃ§in bir frame        self.search_frame = ctk.CTkFrame(self.main_frame, fg_color="gray15")        self.search_frame.pack(pady=10)        # Arama alanÄ±        self.search_entry = ctk.CTkEntry(self.search_frame, placeholder_text="Ara...", width=300, height=30)        self.search_entry.grid(row=0, column=0, padx=(20, 5))  # SaÄŸda buton olduÄŸu iÃ§in saÄŸ padding az        # Arama butonu        self.search_button = ctk.CTkButton(self.search_frame, text="ğŸ”", width=50, height=30, command=self.search_action)        self.search_button.grid(row=0, column=1, padx=(5, 20))  # Solda arama alanÄ± olduÄŸu iÃ§in sol padding az        # Sidebar (Yan Panel)        self.sidebar = ctk.CTkFrame(self.root, width=150, height=400, corner_radius=10, fg_color="gray25")        self.sidebar.place(x=-150, y=10)  # Ä°lk baÅŸta gizlenmiÅŸ olarak baÅŸlÄ±yor        # Sidebar iÃ§eriÄŸi        self.sidebar_button = ctk.CTkButton(self.sidebar, text="MenÃ¼ Butonu")        self.sidebar_button.pack(pady=40, padx=10)        # Sidebar iÃ§indeki toggle butonu        self.toggle_button = None        self.create_toggle_button()        # Ana ekran butonlarÄ±        self.main_button = ctk.CTkButton(self.main_frame, text="Ana Buton")        self.main_button.pack(pady=20)        # Sidebar'Ä± aÃ§Ä±lÄ±ÅŸta animasyonla getirme        self.animate_sidebar(opening=True)    def search_action(self):        """Arama butonuna basÄ±ldÄ±ÄŸÄ±nda Ã§alÄ±ÅŸacak fonksiyon"""        search_text = self.search_entry.get()        print(f"Arama yapÄ±lÄ±yor: {search_text}")  # BurayÄ± kendi ihtiyacÄ±na gÃ¶re gÃ¼ncelleyebilirsin    def animate_sidebar(self, opening=True):        """ Sidebar'Ä± yumuÅŸak bir ÅŸekilde aÃ§Ä±p kapatÄ±r. """        target_x = 10 if opening else -150        current_x = self.sidebar.winfo_x()        step = max(abs(target_x - current_x) // 10, 5)  # Mesafeye baÄŸlÄ± olarak adÄ±m belirleniyor        def step_animation():            nonlocal current_x            if (opening and current_x < target_x) or (not opening and current_x > target_x):                current_x += step if opening else -step                self.sidebar.place(x=current_x, y=10)                self.root.after(10, step_animation)  # 10ms gecikme ile animasyon            else:                self.sidebar.place(x=target_x, y=10)        step_animation()    def create_toggle_button(self):        """ Sidebar gÃ¶rÃ¼nÃ¼mÃ¼ne gÃ¶re toggle butonunu oluÅŸturur. """        if self.toggle_button:            self.toggle_button.place_forget()        if self.sidebar_visible:            self.toggle_button = ctk.CTkButton(self.sidebar, text="X", width=30, height=30, font=("Arial", 14, "bold"),                                               corner_radius=20, fg_color="gray30", hover_color="gray40",                                               text_color="white", command=self.toggle_sidebar)            self.toggle_button.place(x=60, y=0)        else:            self.toggle_button = ctk.CTkButton(self.root, text="â˜°", width=30, height=30, font=("Arial", 14, "bold"),                                               corner_radius=15, fg_color="gray30", hover_color="gray40",                                               text_color="white", command=self.toggle_sidebar)            self.toggle_button.place(x=10, y=10)    def toggle_sidebar(self):        """ Sidebar aÃ§ma/kapatma iÅŸlemi. """        self.animate_sidebar(opening=not self.sidebar_visible)        self.sidebar_visible = not self.sidebar_visible        self.create_toggle_button()