import customtkinter as ctkimport sysimport os# AracRepository ve Arac sınıflarını içe aktarsys.path.append(os.path.abspath(os.path.join(os.getcwd(), '..', 'controllers')))from controllers.arac_controller import AracRepositoryclass AracButtonFactory:    def __init__(self, parent_frame, button_color, hover_color, text_color):        self.parent_frame = parent_frame        self.button_color = button_color        self.hover_color = hover_color        self.text_color = text_color        self.arac_repo = AracRepository()    def create_buttons(self):        araclar = self.arac_repo.get_all_araclar()        for i, arac in enumerate(araclar):            button = ctk.CTkButton(                self.parent_frame,                text=f"{arac.plaka} | {arac.marka} {arac.model}",                fg_color=self.button_color,                hover_color=self.hover_color,                text_color=self.text_color,                corner_radius=12,                width=250,                height=40            )            button.grid(row=i, column=0, padx=20, pady=10, sticky="w")