import customtkinter as ctkimport sysimport os# AracRepository ve Arac sınıflarını içe aktarsys.path.append(os.path.abspath(os.path.join(os.getcwd(), '..', 'controllers')))from controllers.arac_controller import AracRepositoryclass AracButtonFactory:    def __init__(self, parent_frame, button_color, hover_color, text_color, button_size=100, padding=10):        self.parent_frame = parent_frame        self.button_color = button_color        self.hover_color = hover_color        self.text_color = text_color        self.button_size = button_size        self.padding = padding        self.arac_repo = AracRepository()    def create_buttons(self):        araclar = self.arac_repo.get_all_araclar()        # Frame’in genişliğine göre kaç buton sığar, hesaplayalım        frame_width = self.parent_frame.winfo_width()        if frame_width == 1:  # Eğer henüz hesaplanmadıysa            self.parent_frame.update_idletasks()            frame_width = self.parent_frame.winfo_width()        buton_alani = self.button_size + (2 * self.padding)        max_columns = max(1, frame_width // buton_alani)        for widget in self.parent_frame.winfo_children():            widget.destroy()        for index, arac in enumerate(araclar):            row = index // max_columns            column = index % max_columns            button = ctk.CTkButton(                self.parent_frame,                text=arac.plaka,                fg_color=self.button_color,                hover_color=self.hover_color,                text_color=self.text_color,                corner_radius=10,                width=self.button_size,                height=self.button_size            )            button.grid(row=row, column=column, padx=self.padding, pady=self.padding)